import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaItchIo, FaPlay, FaEye } from 'react-icons/fa';
import './Projects.css';

const Projects = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const projects = [
    {
      id: 1,
      title: 'Space Adventure',
      description: 'Космическая аркадная игра с уникальными механиками движения и стрельбы. Разработана на Unity с использованием C#.',
      image: 'https://via.placeholder.com/400x250/00ff88/000000?text=Space+Adventure',
      category: 'unity',
      technologies: ['Unity', 'C#', 'Blender'],
      github: 'https://github.com/yourusername/space-adventure',
      itch: 'https://yourusername.itch.io/space-adventure',
      demo: 'https://yourusername.itch.io/space-adventure',
      featured: true
    },
    {
      id: 2,
      title: 'Pixel Runner',
      description: 'Платформер в пиксельной графике с процедурной генерацией уровней и системой достижений.',
      image: 'https://via.placeholder.com/400x250/00ccff/000000?text=Pixel+Runner',
      category: 'unity',
      technologies: ['Unity', 'C#', 'Photoshop'],
      github: 'https://github.com/yourusername/pixel-runner',
      itch: 'https://yourusername.itch.io/pixel-runner',
      demo: 'https://yourusername.itch.io/pixel-runner'
    },
    {
      id: 3,
      title: 'RPG Quest',
      description: 'Ролевая игра с открытым миром, системой квестов и инвентаря. Разработана на Unreal Engine.',
      image: 'https://via.placeholder.com/400x250/ff6b6b/000000?text=RPG+Quest',
      category: 'unreal',
      technologies: ['Unreal Engine', 'C++', 'Blender'],
      github: 'https://github.com/yourusername/rpg-quest',
      itch: 'https://yourusername.itch.io/rpg-quest',
      demo: 'https://yourusername.itch.io/rpg-quest'
    },
    {
      id: 4,
      title: 'Mobile Puzzle',
      description: 'Мобильная головоломка с интуитивным управлением и красивой графикой.',
      image: 'https://via.placeholder.com/400x250/4ecdc4/000000?text=Mobile+Puzzle',
      category: 'mobile',
      technologies: ['Unity', 'C#', 'Photoshop'],
      github: 'https://github.com/yourusername/mobile-puzzle',
      itch: 'https://yourusername.itch.io/mobile-puzzle',
      demo: 'https://yourusername.itch.io/mobile-puzzle'
    },
    {
      id: 5,
      title: 'VR Experience',
      description: 'Иммерсивный VR-опыт с интерактивными элементами и реалистичной физикой.',
      image: 'https://via.placeholder.com/400x250/a55eea/000000?text=VR+Experience',
      category: 'vr',
      technologies: ['Unity', 'C#', 'VR SDK'],
      github: 'https://github.com/yourusername/vr-experience',
      itch: 'https://yourusername.itch.io/vr-experience',
      demo: 'https://yourusername.itch.io/vr-experience'
    },
    {
      id: 6,
      title: 'Multiplayer Shooter',
      description: 'Многопользовательский шутер с современной графикой и сетевым кодом.',
      image: 'https://via.placeholder.com/400x250/f7b731/000000?text=Multiplayer+Shooter',
      category: 'unreal',
      technologies: ['Unreal Engine', 'C++', 'Networking'],
      github: 'https://github.com/yourusername/multiplayer-shooter',
      itch: 'https://yourusername.itch.io/multiplayer-shooter',
      demo: 'https://yourusername.itch.io/multiplayer-shooter'
    }
  ];

  const filters = [
    { name: 'Все', value: 'all' },
    { name: 'Unity', value: 'unity' },
    { name: 'Unreal', value: 'unreal' },
    { name: 'Мобильные', value: 'mobile' },
    { name: 'VR', value: 'vr' }
  ];

  const filteredProjects = activeFilter === 'all' 
    ? projects 
    : projects.filter(project => project.category === activeFilter);

  return (
    <section id="projects" className="section projects">
      <div className="container">
        <motion.h2
          className="section-title"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Мои проекты
        </motion.h2>

        <motion.div
          className="filters"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {filters.map((filter, index) => (
            <motion.button
              key={index}
              className={`filter-btn ${activeFilter === filter.value ? 'active' : ''}`}
              onClick={() => setActiveFilter(filter.value)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              {filter.name}
            </motion.button>
          ))}
        </motion.div>

        <motion.div
          className="projects-grid"
          layout
        >
          {filteredProjects.map((project, index) => (
            <motion.div
              key={project.id}
              className={`project-card ${project.featured ? 'featured' : ''}`}
              layout
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              whileHover={{ y: -10 }}
            >
              <div className="project-image">
                <img src={project.image} alt={project.title} />
                <div className="project-overlay">
                  <div className="project-links">
                    <motion.a
                      href={project.github}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="project-link"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      <FaGithub />
                    </motion.a>
                    <motion.a
                      href={project.itch}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="project-link"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      <FaItchIo />
                    </motion.a>
                    <motion.a
                      href={project.demo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="project-link"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      <FaPlay />
                    </motion.a>
                  </div>
                </div>
                {project.featured && (
                  <div className="featured-badge">
                    <FaEye />
                  </div>
                )}
              </div>

              <div className="project-content">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="project-technologies">
                  {project.technologies.map((tech, techIndex) => (
                    <span key={techIndex} className="tech-tag">
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
